@@ -0,0 +1,16 @@
+{% assign popovers = site.data.popovers %}
+{% for popover in popovers %}
+<div id="{{ popover.term | slug }}-popover-html" class="popover-template">
+  {{ popover.d | markdownify }}
+</div>
+{% endfor %}
+
+<script type="text/javascript">
+{% for popover in popovers %}
+tippy('#{{ popover.term | slug }}-popover', {
+  html: '#{{ popover.term | slug }}-popover-html',
+  arrow: true,
+  animation: 'fade'
+});
+{% endfor %}
+</script>
\ No newline at end of file

@@ -0,0 +1,23 @@
+{% unless page.no_sidebar %}
+<aside class="sidebar">
+  {% for group in site.data.sidebar.groups %}{% assign docs = group.docs %}
+  <p>
+    {{ group.name }}
+  </p>
+  <ul class="sidebar-items">
+    {% for doc in docs %}
+    {% capture doc_url %}{{ site.baseurl }}docs/{{ group.dir }}/{{ doc.endpoint }}/{% endcapture %}
+    <li{% if doc_url == page.url %} class="active"{% endif %}>
+      <a href="{{ site.baseurl }}docs/{{ group.dir }}/{{ doc.endpoint }}">
+      {{ doc.name }}
+      </a>
+    </li>
+    {% endfor %}
+  </ul>
+  {% endfor %}
+  <hr />
+  <a class="button is-info">
+    Latest version: {{ site.latest }}
+  </a>
+</aside>
+{% endunless %}
\ No newline at end of file

@@ -0,0 +1,6 @@
+{% if page.toc %}
+<div class="toc">
+  <h2 class="title">{{ page.title }}</h2>
+  {{ content | toc_only }}
+</div>
+{% endif %}

@@ -0,0 +1,34 @@
+// Spinning Icons
+// --------------------------
+
+.#{$fa-css-prefix}-spin {
+  -webkit-animation: fa-spin 2s infinite linear;
+          animation: fa-spin 2s infinite linear;
+}
+
+.#{$fa-css-prefix}-pulse {
+  -webkit-animation: fa-spin 1s infinite steps(8);
+          animation: fa-spin 1s infinite steps(8);
+}
+
+@-webkit-keyframes fa-spin {
+  0% {
+    -webkit-transform: rotate(0deg);
+            transform: rotate(0deg);
+  }
+  100% {
+    -webkit-transform: rotate(359deg);
+            transform: rotate(359deg);
+  }
+}
+
+@keyframes fa-spin {
+  0% {
+    -webkit-transform: rotate(0deg);
+            transform: rotate(0deg);
+  }
+  100% {
+    -webkit-transform: rotate(359deg);
+            transform: rotate(359deg);
+  }
+}

@@ -0,0 +1,25 @@
+// Bordered & Pulled
+// -------------------------
+
+.#{$fa-css-prefix}-border {
+  padding: .2em .25em .15em;
+  border: solid .08em $fa-border-color;
+  border-radius: .1em;
+}
+
+.#{$fa-css-prefix}-pull-left { float: left; }
+.#{$fa-css-prefix}-pull-right { float: right; }
+
+.#{$fa-css-prefix} {
+  &.#{$fa-css-prefix}-pull-left { margin-right: .3em; }
+  &.#{$fa-css-prefix}-pull-right { margin-left: .3em; }
+}
+
+/* Deprecated as of 4.4.0 */
+.pull-right { float: right; }
+.pull-left { float: left; }
+
+.#{$fa-css-prefix} {
+  &.pull-left { margin-right: .3em; }
+  &.pull-right { margin-left: .3em; }
+}

@@ -0,0 +1,12 @@
+// Base Class Definition
+// -------------------------
+
+.#{$fa-css-prefix} {
+  display: inline-block;
+  font: normal normal normal #{$fa-font-size-base}/#{$fa-line-height-base} FontAwesome; // shortening font declaration
+  font-size: inherit; // can't have font-size inherit on line above, so need to override
+  text-rendering: auto; // optimizelegibility throws things off #1094
+  -webkit-font-smoothing: antialiased;
+  -moz-osx-font-smoothing: grayscale;
+
+}

