+.toc {
+  width: 300px;
+  border: 1px solid gray;
+  background-color: #f9f9f9;
+  padding-bottom: 15px;
+}
+
+ul.toc li {
+  visibility: hidden;
+  list-style: none;
+}
+
+ul.toc li ul {
+  visibility: visible;
+  counter-reset: item;
+}
+
+ul.toc li ul li {
+  visibility: visible;
+}
+
+ul.toc li ul li:before {
+  content: counters(item, ".") ". ";
+  counter-increment: item;
+}
