+        SubjectCond attrCond = new SubjectCond(AttributeCond.Type.EQ);
+        attrCond.setSchema("username");
+        attrCond.setExpression("rossini");
+        SearchCond simpleCond = SearchCond.getLeafCond(attrCond);
