+    @Override
+    public void create(final User user) {
+        if (user.getKey() == null) {
+            user.setKey(UUID.randomUUID());
+        }
+        if (USERS.containsKey(user.getKey())) {
+            throw new IllegalArgumentException("User already exists: " + user.getKey());
+        }
+        USERS.put(user.getKey(), user);
