+
+        super(BaseModal.CONTENT_ID, pageRef, false);
+        this.baseModal = baseModal;
+        this.application = application;
+
+        disableCheckBoxes();
+        enableExitButton();
+
+        this.addNewItemPanelBuilder(new PrivilegeWizardBuilder(application, new PrivilegeTO(), pageRef), true);
+
