+angular.module('SyncopeEnduserApp')
+        .factory('GenericUtil', ['$resource', '$q',
+          function ($resource, $q) {
+
+            var genericUtil = {};
+
+            genericUtil.getEnclosingFormName = function (element) {
+              var form = $(element).closest('form');
+              if (form.length) {
+                return form[0].name;
+              }
+              return null;
+            };
+
+            genericUtil.getEnclosingFormController = function (element, scope) {
+              var form = $(element).closest('form');
+              if (form.length) {
+                var currentForm = scope[form[0].name];
+                if (currentForm != null) {
+                  return currentForm;
+                }
+              }
+              return null;
+            };
+
+            genericUtil.getEnclosingForm = function (element, scope) {
+              var form = $(element).closest('form');
+              if (form.length) {
+                return form[0];
+              }
+              return null;
+            };
+
+            return genericUtil;
+
+          }]);
