-    protected boolean sequenceDetected(final ICharacterScanner scanner,
-            final char[] sequence, final boolean eofAllowed) {
-        if (sequence[0] == '<') {
-            int c = scanner.read();
-            if (c == '?' || c == '!' || c == '%') {
-                scanner.unread();
-                return false;
-            }
-            int back = 1;
-            try {
-                while (true) {
-                    c = scanner.read();
-                    back++;
-                    if (c == -1) {
-                        return false;
-                    }
-                    if (Character.isWhitespace(c) || c == '>') {
-                        if (prefix == PREFIX) {
-                            return false;
-                        }
-                        break;
-                    } else if (c == ':') {
-                        if (prefix == NO_PREFIX) {
-                            return false;
-                        }
-                        break;
-                    }
-                }
-            } finally {
-                for (int i = 0; i < back; i++) {
-                    scanner.unread();
-                }
-            }
-        } else if (sequence[0] == '>') {
-            // read previous char
-            scanner.unread();
-            scanner.unread();
-            int c = scanner.read();
-            // repair position
-            scanner.read();
-            if (c == '%') {
-                return false;
-            }
-        }
-        return super.sequenceDetected(scanner, sequence, eofAllowed);
-    }
-
-    protected boolean endSequenceDetected(final ICharacterScanner scanner) {
-
-        int c;
-        boolean doubleQuoted = false;
-        boolean singleQuoted = false;
-        char[][] delimiters = scanner.getLegalLineDelimiters();
-        boolean previousWasEscapeCharacter = false;
-        while ((c = scanner.read()) != ICharacterScanner.EOF) {
-            if (c == fEscapeCharacter) {
-                scanner.read();
-            } else if (c == '"' && !singleQuoted) {
-                doubleQuoted = !doubleQuoted;
-            } else if (c == '\'' && !doubleQuoted) {
-                singleQuoted = !singleQuoted;
-            } else if (fEndSequence.length > 0 && c == fEndSequence[0] && !doubleQuoted
