+        // captcha check
+        boolean checked = true;
+        if (SyncopeWebApplication.get().isCaptchaEnabled()) {
+            checked = captcha.check();
+        }
+        if (!checked) {
+            SyncopeEnduserSession.get().error(getString(Constants.CAPTCHA_ERROR));
