+    private BaseTree updateTree() {
+
+        tree = new LinkTree("treeTable", roleTreeBuilder.build()) {
+
+            private static final long serialVersionUID = -5514696922119256101L;
+
+            @Override
+            protected IModel getNodeTextModel(final IModel model) {
