+            PasswordPolicySpec policySpec = it.next();
+            if (policySpec.getMinLength() > fpps.getMinLength()) {
+                fpps.setMinLength(policySpec.getMinLength());
+            }
+
+            if ((policySpec.getMaxLength() != 0) && ((policySpec.getMaxLength() < fpps.getMaxLength()))) {
