+
+        Map<String, PropagationByResource> result = new HashMap<>();
+
+        for (UMembership membership : groupDAO.findUMemberships(group)) {
+            populateTransitiveResources(group, membership.getLeftEnd(), result);
+        }
+
+        return result;
