+
 .toc {
   width: 300px;
   border: 1px solid gray;
   background-color: #f9f9f9;
   padding-bottom: 15px;
 }

-ul.toc li {
-  visibility: hidden;
-  list-style: none;
+ul.toc, ul.toc ul {
+  list-style: decimal;
+  padding:0;
+  float:left;
+  display:block;
+  clear:left;
 }

-ul.toc li ul {
-  visibility: visible;
-  counter-reset: item;
+ul.toc li {
+  clear:left;
+  height:1.05em;
+  position:relative;
+  left: 30px;
 }

 ul.toc li ul li {
-  visibility: visible;
+  left: 0px;
 }

-ul.toc li ul li:before {
-  content: counters(item, ".") ". ";
-  counter-increment: item;
-}
\ No newline at end of file
+ul.toc a {
+  float:left;
+  text-decoration:none;
+}
