+    color: transparent;
+    text-shadow: none;
+    background-color: transparent;
+    border: 0;
+    &:after {
+      content: '';
+      display: block;
+      font-size: 10px;
+      width: 1em;
+      height: 1em;
+      margin-top: -0.5em;
+      border-radius: 0.5em;
