+import org.syncope.client.to.ConfigurationTO;
+import org.syncope.console.rest.ConfigurationsRestClient;
+
+/**
+ * Modal window with Connector form.
+ */
+public class ConfigurationModalPage extends SyncopeModalPage {
+
+    @SpringBean(name = "configurationsRestClient")
+    ConfigurationsRestClient configurationsRestClient;
+    
+    public TextField key;
+    public TextField value;
+    public AjaxButton submit;
+
+    /**
+     * ConfigurationModalPage constructor.
+     * 
+     * @param basePage base
+     * @param modalWindow modal-window
+     * @param configurationTO
+     * @param createFlag true for CREATE and false for UPDATE operation
+     */
+    public ConfigurationModalPage(final BasePage basePage, final ModalWindow window,
+            final ConfigurationTO configurationTO, final boolean createFlag) {
+
+        Form form = new Form("ConfigurationForm", new CompoundPropertyModel(configurationTO));
+
+        form.add(new FeedbackPanel("feedback").setOutputMarkupId(true));
+
+        form.add(key = new TextField("key", new PropertyModel(configurationTO, "confKey")));
+
+        key.setRequired(true);
+
+        form.add(value = new TextField("value", new PropertyModel(configurationTO, "confValue")));
+
+        value.setRequired(true);
+
+        submit = new AjaxButton("submit", new Model<String>(getString("submit"))) {
+
+            @Override
+            protected void onSubmit(AjaxRequestTarget target, Form form) {
+                boolean res = false;
+
+                if (createFlag) {
+                    res = configurationsRestClient.createConfiguration(configurationTO);
+
+                    if (!res)
+                            error(getString("error_insert"));
+
+                } else {
+                    res = configurationsRestClient.updateConfiguration(configurationTO);
+
+                    if (!res) 
+                            error(getString("error_updating"));
+                    
+                }
+
+                if (res) {
+                Configuration callerPage = (Configuration) basePage;
+                callerPage.setOperationResult(true);
+
+                window.close(target);
+                }
+            }
+
+            @Override
+            protected void onError(AjaxRequestTarget target, Form form) {
+                target.addComponent(form.get("feedback"));
+            }
+        };
+
+        form.add(submit);
+
+        add(form);
+    }
+}
