+
+                int responseCode = HttpUtils.ping(Boolean.valueOf(tomcatSsl), tomcatHost, tomcatPort);
+
+                if (responseCode == 200) {
+                    return Status.OK;
+                } else {
+                    error = new StringBuilder("Tomcat URL is offline");
+                    return Status.ERROR;
